sys_ws_operation.operation_script:
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {

    // implement resource here


	var requestUser = request.pathParams.user_name;
	var requestNI_query = request.queryParams.ni_query;

	var requestUserGr = new GlideRecord('sys_user');
	requestUserGr.get('user_name', requestUser);

	var count = new GlideAggregate('x_58872_needit_needit');
	count.addAggregate('COUNT');
	count.addQuery('u_requested_for', requestUserGr.sys_id);
	count.addEncodedQuery(requestNI_query);
	count.groupBy('u_request_type');
	count.query();

	var needitCount = count.getRowCount();
	var categoryRecords = [];
	var categoryCount;

	while(count.next()){
		categoryCount = count.getAggregate('COUNT');
// 		gs.info("Number of records in category " + count.u_request_type.getDisplayValue() + " : " + category_records);
// 		categoryRecords.push( '{"requestType":' + count.u_request_type.getDisplayValue() +', "count":' + categoryCount + '}' );
		categoryRecords.push( {"requestType": count.u_request_type.getDisplayValue(), "count": categoryCount });
	}

// 	gs.info("Total number of records: " + needit_records);



	var body = {};

	body.totalUserNIRecs = needitCount;
	body.catCounts = categoryRecords;
	body.user = {"User name": requestUser, "User ID": requestUserGr.sys_id};

	response.setBody(body);


})(request, response);
